import React, {useEffect, useState} from "react";
import Modal from "react-modal";
import { useDispatch } from "react-redux";
import axios from "axios";
import "./modal.style.css";

// import "./modal.style.css";
/*
const addTransactionAction = (data) => ({
  type: "ADD_TRANSACTION",
  payload: data,
});
export const TransationReducer = (state = [], action) => {
  switch (action.type) {
    case "ADD_TRANSACTION":
      return action.payload;
    default:
      return state;
  }
};*/

const ModalSelling = (props) => {
  const [stockName] = props.holding.stockName;


  const url = "http://localhost:8080/assets/";
  const [sellCount, setSellCount] = useState(1);
  // const [symbol] = holding.symbol;
  const [nowPrice] = props.holding[1].now;
  const [transactionDate, setTransactionDate] = useState(new Date());


  const [purchasePrice, setPurchasePrice] = useState(nowPrice);
useEffect(()=>{
  console.log(props.holding)
},[])
  /*  const dispatch = useDispatch();
  const addTransaction = (newTransaction) =>
    dispatch(addTransactionAction(newTransaction));*/

  const decrease = (e) => {
    e.preventDefault();
    if(sellCount > 1){
      setSellCount(sellCount - 1);
      setPurchasePrice((sellCount - 1) * nowPrice);
    }else{
      alert('올바른 수량을 입력세요.');
    }
  };
  const increase = (e) => {
    e.preventDefault();
    let possetionedCount = JSON.parse(sessionStorage.getItem("users_Asset")).shareCount;
    if(possetionedCount > sellCount) {
      setSellCount(sellCount + 1);
      setPurchasePrice((sellCount + 1) * nowPrice);
      console.log(`selling count ++`);
    } else {
      alert('올바른 수량을 입력하세요.');
    }
  };

  const submitTransaction = (e) => {
    e.preventDefault();
    const newTransaction = {
      userId : sessionStorage.getItem("logined_user").userId,
      stockName : stockName,
      // symbol : symbol,
      sellCount: sellCount,
      nowPrice: nowPrice,
      purchasePrice: purchasePrice,
      transactionDate : new Date(),
      transactionType : "매도"
    };
    console.log(`userId : ${sessionStorage.getItem("logined_user").userId}, stockName : ${stockName} ,sellCount : ${sellCount}, purchasePrice : ${purchasePrice}, 거래일 : ${transactionDate}`)
    axios
      .post(url + `/sell`)
      .then((response) => {
        console.log(`ModalSelling axios then`);
        setSellCount(1);
        setPurchasePrice(props.holding[1].nowPrice);
        // dispatch(addTransactionAction(response.data));
      })
      .catch((error) => {
        console.log(`ModalSelling axios catch`);
        throw error;
      });

  };
  const modalStyle = {
    content: {
      width: "300px",
      height: "400px",
    },
  };
  return (
    <>
      <Modal {...props.holding[1]} style={modalStyle}>
        <span className="text_small ">{props.holding[1].stockName}</span> <br />
        <span className="text_small" style={{ "margin-right": "8px" }}>
          현재가 {stockName}
        </span>
        <span className="text_small ">{props.holding[1].now} 원</span> <br />
        <span className="text_small" style={{ "margin-right": "8px" }}>
          매입가
        </span>
        <span className="text_small ">{purchasePrice} 원</span>
        <h1>{sellCount} 주</h1>
        <div>
          <button
            className="btn btn-default bg-transparent plus_minus_btn btn-rounded btn-raised"
            onClick={decrease}
          >
            {" "}
            -1{" "}
          </button>
          <button
            className="btn btn-default bg-transparent plus_minus_btn btn-rounded btn-raised"
            onClick={increase}
          >
            {" "}
            +1{" "}
          </button>
        </div>
        <tr>
          <td>
            <button
              className="btn btn-default btn-gray btn-rounded"
              onClick={() => !props.isClose}>
              취소
            </button>
          </td>
          <td>
            <button
              className="btn btn-default btn-red btn-rounded"
              onClick={submitTransaction}>
              매도
            </button>
          </td>
        </tr>
      </Modal>
    </>
  );
};

export default ModalSelling;
