import React, {useEffect, useState} from "react";
import { BudgetHistory, TotalBudget, HoldingShares } from "./index";
import "./portfoliopage.style.css";
import Navbar from "../logined_navbar/Navbar";
import Menu from "../menu/Menu";
import axios from "axios";



const PortfolioPage = () => {
  const [holding,setHolding]=useState([]);
  const portfolioContent = [
    { title: "보유종목", content: <HoldingShares holding={holding} setHolding={setHolding}/> },
    { title: "거래내역", content: <BudgetHistory holding={holding} setHolding={setHolding}/> },
  ];
  const [user, setUser] = useState("");


  useEffect(() => {
    axios
      .get(`http://localhost:8080/assets/holdingCount/${JSON.parse(sessionStorage.getItem("logined_user")).userId}`)
      .then((response) => {
        console.log(`${JSON.stringify(response.data.holdingCount)}  : HoldingShares java useEffect then`);
        setHolding(response.data.holdingCount)

      })
      .catch((error) => {
        console.log(`HoldingShares useEffect catch`);
        throw error;
      });
  }, [user]);


  const useTabs = (initialTabs, allTabs) => {
    const [portfolioContentIndex, setPortfolioContentIndex] = useState(
      initialTabs
    );
    return {
      portfolioContentItem: allTabs[portfolioContentIndex],
      portfolioContentChange: setPortfolioContentIndex,
    };
  };
  const { portfolioContentItem, portfolioContentChange } = useTabs(
    0,
    portfolioContent
  );

  const [pageArr, setPageArr] = useState([]);
  const [prev, setPrev ] = useState(false);
  const [next, setNext ] = useState(false);
  const [page, setPage ] = useState(1);
  const [range, setRange] = useState(1);

  const clickNext = () => {
    getAll(pageArr[0] + 5 , range + 1);
  }
  const clickPrev = () => {
    getAll(pageArr[0] - 1 , range - 1);
  }
  const getAll = (page, range) => {
    setPage(page);
    setRange(range);
    setPageArr([]);
    setHolding([]);
    axios
      .get(`http://localhost:8080/assets/pagination/${page}/${range}`)
      .then((response)=>{
          console.log(`${JSON.stringify(response.data)}   :    /assets/pagination/${page}/${range}`)
          response.data.list.map((item) => {
            setHolding((holding) => [...holding, item]);
          })
          let i = 0
          const startPage = response.data.pagination.startPage
          const endPage = response.data.pagination.endPage
          if (
            response.data.pagination.pageCnt <
            startPage + response.data.pagination.rangeSize
          ) {
            for (i; i < response.data.pagination.pageCnt - startPage + 1; i++)
              setPageArr((pageArr) => [...pageArr, startPage + i])
          } else {
            for (i; i < response.data.pagination.rangeSize; i++)
              setPageArr((pageArr) => [...pageArr, startPage + i])
          }
          setPrev(response.data.pagination.prev)
          setNext(response.data.pagination.next)
        }
      )
      .catch((error)=>console.log('error'))
  }
  useEffect(()=>{
    getAll(1, 1)
  },[])
  return (
    <>
      <Navbar />
      <div className="content-container">
        <div className="wrapper">
          <Menu />
          <div>
            <div className="documentroom_container">
              <div className="documentroom_text">포트폴리오</div>
              <TotalBudget />
              <div className="tab_portfolio_container">
                {portfolioContent.map((section, index) => (
                  <button
                    onClick={() => portfolioContentChange(index)}
                    className="link-list-tab"
                  >
                    {section.title}
                  </button>
                ))}
              </div>

              <div className="tab_portfolio_content_container">
                {portfolioContentItem.content}
                <div className="pagination-div">
                  <div className="pagination">
                    {prev && (
                      <div className="page_button" id="prev" onClick={clickPrev}>
                        이전
                      </div>
                    )}

                    {pageArr.map((pagenum) => (
                      <div
                        className="page_button"
                        key={pagenum}
                        onClick={() => {
                          getAll(pagenum, range);
                        }}
                      >
                        {pagenum}
                      </div>
                    ))}

                    {next && (
                      <div className="page_button" id="next" onClick={clickNext}>
                        다음
                      </div>
                    )}
                  </div>
                </div>
              </div>

            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default PortfolioPage;
