import React, {useState, useEffect, useContext} from "react";
import axios from "axios";
import "./totalbudget.style.css";
import {AssetContext} from "../../../../context";

const TotalBudget = (props) => {

  const {asset, setAsset} = useContext(AssetContext);
  const [total, setTotal] = useState(0);
  const [totalProfit, setTotalProfit] = useState(0);
  const [totalRatio, setTotalRatio] = useState(0);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/assets/holdingCount/${JSON.parse(sessionStorage.getItem("logined_user")).userId}`)
      .then((response) => {
        setAsset(response.data.holdingCount);
        console.log(response.data.holdingCount[0].totalAsset);
      })
      .catch((error) => {
        throw error;
      });
  }, []);


 /* useEffect(() => {
    console.log(typeof total)
    axios
      .get(`http://localhost:8080/assets/total/${JSON.parse(sessionStorage.getItem("logined_user")).userId}`)
      .then(({data}) => {
        console.log(`~~~~~~~~~~total~~~~~~~${JSON.stringify(data.totalAmount)}`);
        if(data.totalAmount.length > 0){
          setTotal(data.totalAmount.totalAsset);
          setTotalProfit(data.totalAmount.totalProfit);
          setTotalRatio(data.totalAmount.totalProfitRatio);
        }
      })
      .catch((error) => {
        console.log(`TotalBudget useEffect catch`);
        throw error;
      });
  }, []);*/


  return (
    <>
      <div>
        <div className="totalbudget_section">
          <div>
            <div className="my_totlabudget_title"> 내 자산총액</div>
            <div className="my_totlabudget_money">{asset[0].totalAsset}원</div>
          </div>
          <div className="my_totlabudget_title">
            <div className="my_totlabudget_money">
              <span>평가 수익률</span>
              <span> : {totalRatio} %</span>
            </div>

            <div className="money">
              <div className="my_totlabudget_title"><span>평가 손익</span></div>
              <span className="my_totlabudget_money"> : {totalProfit} 원</span>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default TotalBudget;
