import React, { Component } from "react";

import edit from "../../../../assets/issue/issue_edit.png";
import comment from "../../../../assets/issue/issue_comment.png";
import file from "../../../../assets/issue/issue_file.png";
import conference from "../../../../assets/issue/issue_video_conference.png";
import IssueDetailDialog from "./IssueDetailDialog";
import * as service from "../../../../services/IssueService";
import { Draggable } from "react-beautiful-dnd";
import {
  Container,
  IssueItemDetail,
  IssueTitles,
  IssueContents,
  IssueContent,
  CommentCount
} from "./issueItem.style";

export class IssueItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      mouseCheck: false
    };
  }
  openDialog = () => {
    localStorage.setItem("issueId", this.props.task._id);
    console.log("issueId", localStorage.getItem("issueId"));

    this.setState({
      open: true
    });
  };
  onCloseModal = () => {
    this.setState({ open: false });
  };
  componentDidMount() {}

  render() {
    const { task, index, status } = this.props;

    return (
      <React.Fragment>
        <Draggable draggableId={task._id} index={index}>
          {(provided, snapshot) => (
            <Container
              onMouseOver={this.editAppear}
              onMouseOut={this.editDisapear}
              {...provided.draggableProps}
              {...provided.dragHandleProps}
              ref={provided.innerRef}
              isDragging={snapshot.isDragging}
            >
              <IssueTitles onClick={this.openDialog}>{task.title}</IssueTitles>
              <IssueContents>
                {task.content}
                <img
                  width="7px"
                  height="12px"
                  style={{ marginLeft: "4px" }}
                  src={conference}
                />
              </IssueContents>

              <IssueItemDetail>
                <img
                  onClick={this.openDialog}
                  width="13px"
                  height="12px"
                  src={comment}
                />
                <CommentCount>{task.commentIds.length}</CommentCount>
                <img width="13px" height="12px" src={file} />
              </IssueItemDetail>
            </Container>
          )}
        </Draggable>
        <IssueDetailDialog
          key={task._id}
          task={task}
          status={status}
          open={this.state.open}
          onCloseModal={this.onCloseModal}
        />
      </React.Fragment>
    );
  }
}

export default IssueItem;
