/**
 * [OUTLINE]
 * 담당자 : 안지후
 * 로그인 화면 구성
 * 수정 : 조윤영 - 현재 Googlye 및 KaKao 연동 Login은 서버상 도메인에서만 가능함.
 */

import React, { Component } from "react";
import { withRouter } from "react-router-dom";

import google from "../../../../assets/signIn/google.png";
import kakao from "../../../../assets/signIn/kakao.png";
import {
  SignInContainer,
  LoginContainer,
  SignInHeader,
  InputGroup,
  Row,
  Label,
  WarnId,
  InputBorder,
  LoginInput,
  WarnPassword,
  Checks,
  Check,
  LoginLinkContainer,
  OriginLoginBtn,
  Buttons,
  ImgBtnContainer,
  OtherLoginBtn,
  Img,
  ForgetBtn
} from "./signin.style";

import GoogleLogin from "react-google-login";
import KakaoLogin from "react-kakao-login";

import * as service from "../../../../services/SignInService";

const GoogleKey =
  "419409692345-cjddji3koajma5occofknl50cl27scie.apps.googleusercontent.com";
const KaKaoKey = "e5e36f8b6e47e733a86feb3afb06e56c";

class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: false,
      checked: false,
      id: "",
      name: "",
      provider: "",
      email: "",
      password: "",
      isWarnId: true,
      isWarnPassword: true
    };
  }
  componentDidCatch(error, info) {
    this.setState({
      error: true
    });
  }

  submitLogin(e) {}

  handleChange = e => {
    const {
      target: { checked }
    } = e;
    this.setState({ checked });
  };

  /*Google 로그인 함수 */
  responseGoogle = res => {
    /*Google로그인 성공할 경우 */
    this.setState({
      id: res.googleId,
      email: res.profileObj.email,
      name: res.profileObj.name,
      provider: "google"
    });
    console.log(res);
  };
  responseFail = err => {
    /*Login Fail*/
    console.error(err);
  };

  /*Kakao 로그인 함수 */
  success = response => {
    /*Kakao 로그인 성공할 경우 */
    console.log(response);
  };

  failure = error => {
    /*Kakao 로그인 실패할 경우 */
    console.log(error);
  };

  /*일반 로그인 함수 */
  originLogin = e => {
    console.log("[일반 로그인 선택]");
    console.log("이메일:" + this.state.email);
    console.log("비밀번호:" + this.state.password);

    service.signIn(this.state.email, this.state.password).then(
      res => {
        this.props.history.push("/auth/projectManage");
      },
      err => {
        console.log("로그인 실패");

        document.getElementById("warnId").style.display = this.state.isWarnId
          ? "inline"
          : "none";
        document.getElementById("warnPwd").style.display = this.state.isWarnId
          ? "inline"
          : "none";
      }
    );
  };

  componentDidMount = () => {
    // 외부 라이브러리 연동: D3, masonry, etc
    // 컴포넌트에서 필요한 데이터 요청: Ajax, GraphQL, etc
    // DOM 에 관련된 작업: 스크롤 설정, 크기 읽어오기 등
    /*이 API 는 여러분의 컴포넌트가 화면에 나타나게 됐을 때 호출됩니다.
  여기선 주로 D3, masonry 처럼 DOM 을 사용해야하는 외부 라이브러리 연동을 하거나,
  해당 컴포넌트에서 필요로하는 데이터를 요청하기 위해 axios, fetch 등을 통하여 ajax 요청을 하거나,
   DOM 의 속성을 읽거나 직접 변경하는 작업을 진행합니다.*/
  };
  componentWillReceiveProps = () => {
    /*이 API 는 컴포넌트가 새로운 props 를 받게됐을 때 호출됩니다.
    이 안에서는 주로, state 가 props 에 따라 변해야 하는 로직을 작성합니다.
    새로 받게될 props 는 nextProps 로 조회 할 수 있으며,
    이 때 this.props 를 조회하면 업데이트 되기 전의 API 이니 참고하세요.*/
  };
  componentWillUpdate = () => {
    /*이 API는 shouldComponentUpdate 에서 true 를 반환했을때만 호출됩니다.
    만약에 false 를 반환했었다면 이 함수는 호출되지 않습니다.
    여기선 주로 애니메이션 효과를 초기화하거나, 이벤트 리스너를 없애는 작업을 합니다.
    이 함수가 호출되고난 다음에는, render() 가 호출됩니다.*/
  };

  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  render() {
    const { id, name, email, password } = this.state;

    if (this.state.error) return <h1>에러발생!</h1>;
    return (
      <SignInContainer>
        <LoginContainer>
          <SignInHeader>로그인</SignInHeader>
          <InputGroup>
            <Row>
              <Label>아이디</Label>
              <WarnId id="warnId">*이메일이 올바르지 않습니다.</WarnId>
            </Row>
            <InputBorder>
              <LoginInput
                type="text"
                name="email"
                placeholder="이메일 형식의 아이디를 입력해주세요"
                onChange={this.change}
              />
            </InputBorder>
          </InputGroup>

          <InputGroup>
            <Row>
              <Label>비밀번호</Label>
              <WarnPassword id="warnPwd">
                * 비밀번호가 올바르지 않습니다.
              </WarnPassword>
            </Row>
            <InputBorder>
              <LoginInput
                name="password"
                placeholder="비밀번호를 입력해주세요"
                onChange={this.change}
              />
            </InputBorder>
          </InputGroup>

          <Checks>
            <Row>
              <input
                onChange={this.handleChange}
                id={this.id}
                type="checkbox"
                checked={this.state.Checked}
              />
              <Check>로그인 상태 유지</Check>
            </Row>

            <Row>
              <input
                onChange={this.handleChange}
                id={this.id}
                type="checkbox"
                checked={this.state.Checked}
              />
              <Check>이메일 기억하기</Check>
            </Row>
          </Checks>

          <LoginLinkContainer>
            <OriginLoginBtn onClick={this.originLogin}>로그인</OriginLoginBtn>
          </LoginLinkContainer>

          <Buttons>
            <GoogleLogin
              clientId={GoogleKey}
              onSuccess={this.responseGoogle}
              onFailure={this.responseGoogle}
              cookiePolicy={"single_host_origin"}
              render={renderProps => (
                <ImgBtnContainer>
                  <Img src={google} />

                  <OtherLoginBtn
                    onClick={renderProps.onClick}
                    disabled={renderProps.disabled}
                  >
                    Google 로그인
                  </OtherLoginBtn>
                </ImgBtnContainer>
              )}
            />

            <KakaoLogin
              jsKey={KaKaoKey}
              onSuccess={this.success}
              onFailure={this.failure}
              render={props => (
                <ImgBtnContainer>
                  <Img src={kakao} />

                  <OtherLoginBtn
                    onClick={e => {
                      e.preventDefault();
                      props.onClick();
                    }}
                  >
                    카카오 로그인
                  </OtherLoginBtn>
                </ImgBtnContainer>
              )}
            />
          </Buttons>

          <ForgetBtn onClick={this.submitLogin.bind(this)}>
            아이디/비밀번호를 잊으셨나요?
          </ForgetBtn>
        </LoginContainer>
      </SignInContainer>
    );
  }
}

export default withRouter(SignIn);
