import React, {useContext, useEffect, useState} from "react";
import "./asset.style.css";
import {AssetContext} from "../../../../context";
import axios from "axios";

const Asset = () => {
  const {asset, setAsset} = useContext(AssetContext);
  const [userAsset, setUserAsset] = useState(0);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/assets/holdingCount/${JSON.parse(sessionStorage.getItem("logined_user")).userId}`)
      .then((response) => {
        setAsset(response.data.holdingCount[0]);
        console.log(response.data.holdingCount[0].totalAsset);
      })
      .catch((error) => {
        throw error;
      });
  }, []);


  useEffect(() => {
    setUserAsset(asset.totalAsset);
    console.log(asset);
    console.log(asset.totalAsset)
  },[asset])


  return (
    <div>
      <div className="asset_title_section">
        <div className="my_asset_title"> 내 자산총액</div>
        <div className="today">{userAsset}</div>
      </div>

      <div className="my_money"> 원</div>
      <div>
        <div className="money">
          <span>평가 수익률</span>
          <span> :  %</span>
        </div>

        <div className="money">
          <span>평가 손익</span>
          <span className="won"> :  원</span>
        </div>
      </div>
    </div>
  );
};

export default Asset;
